                            (actualNode, errorName) => CheckOr(actualNode, state, errorName,
                                (actualNode, errorName) => CheckDefinition_WhiteSpace(actualNode, state, errorName),
                                (actualNode, errorName) => CheckDefinition_Comment(actualNode, state, errorName)
                            )
                        )
                    )
                );
            Int32 foundPosition = state.Position;
            if (result && ((actualNode.Children.Count > 0) || (actualNode.Text != "")))
                parentNode.AddChild(actualNode);
            return result;
        }

    }
}